using System.Collections;using System.Collections.Generic;using UnityEngine;/// <summary>/// 敵キャラ側の接触判定/// </summary>public class EnemyCollisionTrigger : MonoBehaviour {    [HideInInspector] public bool isOn = false; // 判定内に敵か壁があるか    private string groundTag = "Ground";    private string enemyTag = "Enemy";    private void OnTriggerEnter2D(Collider2D collision) {        if (collision.tag == groundTag || collision.tag == enemyTag) {            isOn = true;        }    }    private void OnTriggerExit2D(Collider2D collision) {        if (collision.tag == groundTag || collision.tag == enemyTag) {            isOn = false;        }    }}